import React, { useState } from 'react';
import { Typography, TextField, Button } from '@material-ui/core';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [token, setToken] = useState('');
    const [userData, setUserData] = useState({});
    const login = require('../services/login').loginForm;


    const clearForm = () => {
        setErrorMessage('');
        setEmail('');
        setPassword('');
        setErrorMessage('');
    }



    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Perform validation
        if (!email || !password) {
            setErrorMessage('Please enter both email and password');
            return;
        }


        const loginSuccess = login(email, password, setToken, setUserData, setErrorMessage);
        // Clear form fields
        if (loginSuccess) {
            clearForm();
        }
    };

    return (
        <div className="login-container">
            <Typography variant="h1">Login Page</Typography>
            <form onSubmit={handleSubmit} className="login-form">
                <div className="form-field">
                    <label>Email:</label>
                    <TextField type="email" value={email} onChange={handleEmailChange} />
                </div>
                <div className="form-field">
                    <label>Password:</label>
                    <TextField type="password" value={password} onChange={handlePasswordChange} />
                </div>
                {errorMessage && <p>{errorMessage}</p>}
                <Button type="submit" variant="contained" color="primary">
                    Login
                </Button>
            </form>
            {token ? <p>Logged in and token is {token}</p> : <p>Not logged in</p>}
            {userData.displayName ? <p>Welcome {userData.displayName}</p> : null}
        </div>
    );
};

export default LoginPage;
