import axios from 'axios';
import React, { useState } from 'react';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [token, setToken] = useState('');
    const [userData, setUserData] = useState({});
    const login = require('../services/login').loginForm;


    const clearForm = () => {
        setErrorMessage('');
        setEmail('');
        setPassword('');
        setErrorMessage('');
    }



    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Perform validation
        if (!email || !password) {
            setErrorMessage('Please enter both email and password');
            return;
        }

        // Call API to login

        const loginSuccess = login(email, password, setToken, setUserData, setErrorMessage);
        // Clear form fields
        if(loginSuccess){
            clearForm();
        }  
    };

    return (
        <div>
            <h1>Login Page</h1>
            <form onSubmit={handleSubmit}>
            <div>
                <label>Email:</label>
                <input type="email" value={email} onChange={handleEmailChange} />
            </div>
            <div>
                <label>Password:</label>
                <input type="password" value={password} onChange={handlePasswordChange} />
            </div>
            {errorMessage && <p>{errorMessage}</p>}
            <button type="submit">Login</button>
            </form>
            {token ? <p>logged in and token is {token}</p> : <p>not logged in</p>}
            {userData.displayName ? <p>Welcome {userData.displayName}</p> : null}
        </div>
    );
};

export default LoginPage;
